import requests

def calculate_length(data):
    count = 0
    for x, y in data.items():
        count += len(x) + len(y) + 2
    return count - 1

cyclic= b""

data = {'hideSsis': b'1',
        'ssid':b'1',
        'security':b'1',
        'wrlPwd':b'1',
        'hideSsid_5g':b'1',
        'ssid_5g':b'1',
        'security_5g':cyclic,
        'wrlPwd_5g':b'1'}

headers = {
    'Host': '192.168.88.128',
    'Content-Length': f'{calculate_length(data)}',
    'Content-Type': 'application/x-www-form-urlencoded',
    'Cookie': 'password=1234',
    'User-Agent':
    'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/108.0.5359.125 Safari/537.36',
    'Accept':
    'text/html,application/xhtml+xml,application/xml;q=0.9,*/*;q=0.8',
    'Accept-Encoding': 'gzip, deflate',
    'Accept-Language': 'zh-CN,zh;q=0.9',
    'Upgrade-Insecure-Requests': '1',
    'Connection': 'close'
}

url = 'http://192.168.88.128/goform/WifiBasicSet'

res = requests.post(url=url, headers=headers, data=data, verify=False)

print(res)